import React, { useState } from 'react';
import styles from "./Crew.module.css";
import crew from "./CrewMembers";

const Crew = () => {
  const numOfCrew = crew.length;
  const [indexCrew,setIndexCrew] = useState(0);
  const [focusCrewMember,setFocusCrewMember] = useState(crew[indexCrew]);

  const [pic,setPic] =useState(focusCrewMember.pic);
  const [desc,setDesc] = useState(focusCrewMember.description);
  const [name,setName] = useState(focusCrewMember.name);
  const [pos,setPos] = useState(focusCrewMember.position);


  const [classDouglas,setClassDouglas] =useState(styles.radioActive);
  const [classMark,setClassMark]= useState('');
  const [classVictor,setClassVictor]=useState('');
  const [classAnsari,setClassAnsari] = useState('');

  const emptyAllRadioClassName = ()=>{
      setClassDouglas('');
      setClassVictor('');
      setClassMark('');
      setClassAnsari('');
  }

  const activeRadioBTN=(focusCrewMember)=>
  {
    emptyAllRadioClassName();
    if(focusCrewMember.name==="Douglas Hurley")
    {
      setClassDouglas(styles.radioActive);
    }else if(focusCrewMember.name==="Victor Glover")
    {
      setClassVictor(styles.radioActive);
    }else if(focusCrewMember.name==="MARK SHUTTLEWORTH")
    {
      setClassMark(styles.radioActive);
    }else if(focusCrewMember.name==="Anousheh Ansari")
    {
      setClassAnsari(styles.radioActive);
    }
  }

  const radioClickHandler = (index)=>{
    setIndexCrew(index);
    setFocusCrewMember(crew[index]);

    //Setting active radio button
    activeRadioBTN(focusCrewMember);
  }


  // function incrIndex()
  // {
  //   if(indexCrew>=numOfCrew || indexCrew<0)
  //   {
  //     setIndexCrew(0);
  //   }else{
  //     setIndexCrew(indexCrew++);
  //   }
  // }
  // function decrIndex()
  // {
  //   if(indexCrew>=numOfCrew || indexCrew<=0)
  //   {
  //     setIndexCrew(numOfCrew);
  //   }else{
  //     setIndexCrew(indexCrew--);
  //   }
  // }

  return (
    <div className={styles.page}>
      <div className={styles.content}>
        <h5 className={styles.meetCrew}>
          MEET YOUR CREW
        </h5>
        <div className={styles.crewMemberPic}>
          <img src={pic[1]} alt={`${name} image`} srcset={`${pic[0]} 3x`} />
          <span className={styles.crewMemberPicUnderline}>&nbsp;</span>
        </div>
        <nav>
            <span className={classDouglas} onClick={()=>{ radioClickHandler(0) }}>&nbsp;</span>
            <span className={classVictor} onClick={()=>{radioClickHandler(1)}}>&nbsp;</span>
            <span className={classVictor} onClick={()=>{radioClickHandler(2)}}>&nbsp;</span>
            <span className={classAnsari} onClick={()=>{radioClickHandler(3)}}>&nbsp;</span>
            <p>{indexCrew}</p>
          </nav>
          <div id={styles.posName}>
            <p id={styles.position}>{pos}</p>
            <h4 id={styles.name}>{name}</h4>
          </div>
          <p className={styles.des}>{desc}</p>
      </div>
    </div>
  )
}

export default Crew;
